package loop;

public class NestingUsingForLoops {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		for(int j = 1; j <= 5; j++) {
			for(int i = 1; i<= 10; i++) {
				System.out.print(i +" ");
			}
			System.out.println();                  //this is for a new line
		}
		
		
		
		
		
		
		
		
		
	}

}
