package operators;

public class IncrimationAndDecrimation {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

int a = 23;
int b = 45;

int c = b++;
int d = b;;
System.out.println(c + "  " + b);
System.out.println(d + "   " + b);

int e = b--;
System.out.println(e + "   " + b);

	}

}
